function WriteEPS(name, prpath,flags)%function WriteEPS(name, prpath,flags)%Write an eps file with the current figure.  If no path is specified,%the default path (set in the global DEFAULT_PRINT_FOLDER) is used.  %If that is not specified, the current directory is used.%Leave off the .eps.%Don't put spaces in the path -- it doesn't like it.%use SetDefaultPrintFolder(path) to set the default path.global DEFAULT_PRINT_FOLDER;if ~exist('flags')	flags='';endif ~exist('prpath')	prpath=DEFAULT_PRINT_FOLDER;endif isempty(prpath)	path=cd;endif strcmp(prpath(end),filesep)==0    prpath=[prpath filesep];enddisp(['Printing figure to file '])disp([prpath name '.eps'])disp(['using flags ' flags])eval(['print -depsc -adobecset ' flags ' ' prpath name '.eps'])%Now disp the string to paste into latexdisp(['\figtwo{6 in}{' name '}{ }'])